import React, { useState } from 'react';
import InputForm from './components/Input/InputForm';
import './App.css';


function App() {
  const addUserHandler = (enteredValue) => {
    const [enteredUsers, setUsers] = useState
    setUsers(prevUsers => {
      const updatedUsers = [...prevUsers];
      updatedUsers.unshift({ text: enteredValue, id: Math.random().toString() });
      return updatedUsers;
    });

  }
  return (
    <div>
      <section id="goal-form">
        <InputForm onAddUser={addUserHandler} />
      </section>
    </div>
  );
}

export default App;
